import React from 'react'
import { useForm } from "react-hook-form";
import InputField from '../../components/Form/InputField';
import Button from '../../components/Form/Button';

export default function LoginPage() {
  const { 
    register, handleSubmit, watch, formState: { errors } 
  } = useForm({
    defaultValues:{
      username: '',
      password: ''
    }
  });
  
  const onSubmit = data => console.log(data);
  const username = watch('username');
  const password = watch('password');

  return (
    <div>
      <main>
        <div className="mx-auto max-w-8xl px-4 py-6 sm:px-6 lg:px-8 bg-slate-50">
            <div className="grid grid-cols-1 md:grid-cols-1 gap-4">
                <div className="bg-inherit p-4">
                  <div className='font-mono font-semibold'>
                    <h1 className="text-center text-3xl font-semibold font-mono">Login Page</h1>
                      <form onSubmit={handleSubmit(onSubmit)}>
                          <div className='mt-5'>
                              <label for="username" class="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Username: </label>
                              {/* <InputField type='text' placeholder="Username" name={username} {...register("username", { required: "Usernames is required" })} /> */}
                              <InputField
        type="text"
        placeholder="Username"
        name="username"
        register={register}
        validation={{ required: 'Username is required' }}
      />
                              <p>{errors.username?.message}</p>
                          </div>
                          
                          <div className='mt-5'>
                            <label for="password" class="block mb-2 text-sm font-medium text-gray-900 dark:text-white">Password: </label>
                            <InputField type={password} {...register("password", { required: "Password is required" }, { pattern: /^[A-Za-z]+$/i })} placeholder='Password'/>
                            <p>{errors.password?.message}</p>
                          </div>

                          <div className='mt-5'>
                            <Button />
                          </div>
                        </form>
                  </div>
                </div>
            </div>
        </div>
      </main>
    </div>
  )
}
