const API_URL = process.env.REACT_APP_API_URL;
const token = localStorage.getItem('accessToken');

console.log('token: ',token)

export const loginUser = async (data) => {
  try {
    const res = await fetch(`${API_URL}/api/auth/login`, {
        method: 'POST',
        headers: {
            "Content-Type": "application/json",
        },
        credentials: 'include',
        body: JSON.stringify(data)    
    })
    
    if (!res.ok) {
        throw new Error('Failed to fetch user profile');
    }

    const result = await res.json()
    return { 
        message: result.message,
        status: result.status,
        accesToken: result.accessToken,
        userRole: result.userRole
    }
} catch (error) {
    console.log('Error signing in: ', error);
    throw error;
  }
}

const fetchNewAccessToken = async () => {
  try {
    const response = await fetch(`${API_URL}/api/refresh`, {
      method: 'GET',
      credentials: 'include', // Send cookies with the request
    });

    if (response.ok) {
      const data = await response.json();
      console.log('New access token:', data.accessToken);

      // Store the new access token in localStorage
      localStorage.setItem('accessToken', data.accessToken);

      return data.accessToken;
    } else {
      // If the refresh token is expired or invalid
      if (response.status === 401) {
        console.warn('Refresh token expired or invalid. Logging out.');
        localStorage.removeItem('accessToken');
        localStorage.removeItem('refreshToken'); // If you store it separately
        // Redirect to login page or handle logout
        window.location.href = '/login';
      }
      throw new Error('Failed to fetch new access token');
    }
  } catch (error) {
    console.error('Error:', error);
    // Handle error appropriately, possibly by redirecting to login
    throw error;
  }
};

export const fetchWithAuth = async (url, options = {}) => {
  try {
    let accessToken = localStorage.getItem('accessToken');

    if (!accessToken) throw new Error('No access token found');

    options.headers = {
      ...options.headers,
      'Authorization': `Bearer ${accessToken}`,
    };

    let res = await fetch(url, options);

    if (res.status === 403) {
      console.log('Access token expired, attempting to refresh...');
      accessToken = await fetchNewAccessToken();

      if (accessToken) {
        options.headers['Authorization'] = `Bearer ${accessToken}`;
        res = await fetch(url, options);
      } else {
        throw new Error('Failed to refresh token');
      }
    }

    if (!res.ok) {
      const errorMessage = await res.text();
      throw new Error(`Request failed with status ${res.status}: ${errorMessage}`);
    }

    return await res.json();
  } catch (error) {
    console.error('Error fetching with auth:', error);
    throw error;
  }
};
