const API_URL = process.env.REACT_APP_API_URL;

// Log the current token for debugging purposes
const token = localStorage.getItem('accessToken');
console.log('token: ', token);

// Login function to authenticate the user and save the access token
export const loginUser = async (data) => {
  try {
    const res = await fetch(`${API_URL}/api/auth/login`, {
      method: 'POST',
      headers: {
        "Content-Type": "application/json",
      },
      credentials: 'include',
      body: JSON.stringify(data)
    });

    if (!res.ok) {
      throw new Error('Failed to fetch user profile');
    }

    const result = await res.json();
    localStorage.setItem('accessToken', result.accessToken);

    return {
      message: result.message,
      status: result.status,
      accessToken: result.accessToken,
      userRole: result.userRole
    };
  } catch (error) {
    console.log('Error signing in: ', error);
    throw error;
  }
};

// Helper function to get the token from localStorage
const getTokenFromLocalStorage = () => {
  return localStorage.getItem('accessToken');
};

// Helper function to save the token to localStorage
const addTokenToLocalStorage = (token) => {
  localStorage.setItem('accessToken', token);
};

// Function to fetch a new access token using the refresh token
const fetchNewAccessToken = async () => {
  try {
    const response = await fetch(`${API_URL}/api/refresh`, {
      method: 'GET',
      credentials: 'include',
    });

    if (response.ok) {
      const data = await response.json();
      console.log('New access token:', data.accessToken);

      addTokenToLocalStorage(data.accessToken);

      return data.accessToken;
    } else {
      console.log('Failed to fetch new access token, status:', response.status);
      throw new Error('Failed to fetch new access token');
    }
  } catch (error) {
    console.error('Error fetching new access token:', error);
    throw error;
  }
};

// Custom fetch function with request/response interceptors
export const customFetch = async (url, options = {}) => {
  try {
    let token = getTokenFromLocalStorage();
    if (token) {
      options.headers = {
        ...options.headers,
        'Authorization': `Bearer ${token}`,
      };
    }

    let response = await fetch(url, options);

    // Check if the response status indicates that the token is expired
    if (response.status === 403 && !options._retry) {
      console.log('Access token expired, attempting to refresh...');

      options._retry = true; // Prevents infinite loop in case of continuous 403 errors

      const newAccessToken = await fetchNewAccessToken();

      if (newAccessToken) {
        options.headers['Authorization'] = `Bearer ${newAccessToken}`;
        response = await fetch(url, options);
      } else {
        throw new Error('Failed to refresh token');
      }
    }

    if (!response.ok) {
      const errorMessage = await response.text();
      throw new Error(`Request failed with status ${response.status}: ${errorMessage}`);
    }

    return await response.json();
  } catch (error) {
    console.error('Error fetching with auth:', error);
    throw error;
  }
};
