import React from 'react'
import Button from '../../components/Form/Button';
import { Link } from 'react-router-dom';
import { useUsers } from '../../hooks/useUsers';

export default function Dashboard() {
    const { users, loading, error } = useUsers();

    if (loading) {
        return <div>Loading....</div>
    }

    if (error) {
        return <div>Error: {error}</div>
    }

    // dummy data
    // const studentData = [
    //     {
    //         idno: 1,
    //         image: 'test1',
    //         firstname: 'test1',
    //         lastname: 'test1',
    //         username: 'test1',
    //         role: 'test1'
    //     },
    //     {
    //         idno: 2,
    //         image: 'test2',
    //         firstname: 'test2',
    //         lastname: 'test2',
    //         username: 'test2',
    //         role: 'test2'
    //     },
    //     {
    //         idno: 3,
    //         image: 'test3',
    //         firstname: 'test3',
    //         lastname: 'test3',
    //         username: 'test3',
    //         role: 'test3'
    //     }
    // ]

    return (
        <div>
            <main>
            <div class="relative z-10" aria-labelledby="modal-title" role="dialog" aria-modal="true">

  <div class="fixed inset-0 bg-gray-500 bg-opacity-75 transition-opacity" aria-hidden="true"></div>

  <div class="fixed inset-0 z-10 w-screen overflow-y-auto">
    <div class="flex min-h-full items-end justify-center p-4 text-center sm:items-center sm:p-0">

      <div class="relative transform overflow-hidden rounded-lg bg-white text-left shadow-xl transition-all sm:my-8 sm:w-full sm:max-w-lg">
        <div class="bg-white px-4 pb-4 pt-5 sm:p-6 sm:pb-4">
          <div class="sm:flex sm:items-start">
            <div class="mx-auto flex h-12 w-12 flex-shrink-0 items-center justify-center rounded-full bg-red-100 sm:mx-0 sm:h-10 sm:w-10">
              <svg class="h-6 w-6 text-red-600" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor" aria-hidden="true">
                <path stroke-linecap="round" stroke-linejoin="round" d="M12 9v3.75m-9.303 3.376c-.866 1.5.217 3.374 1.948 3.374h14.71c1.73 0 2.813-1.874 1.948-3.374L13.949 3.378c-.866-1.5-3.032-1.5-3.898 0L2.697 16.126zM12 15.75h.007v.008H12v-.008z" />
              </svg>
            </div>
            <div class="mt-3 text-center sm:ml-4 sm:mt-0 sm:text-left">
              <h3 class="text-base font-semibold leading-6 text-gray-900" id="modal-title">Deactivate account</h3>
              <div class="mt-2">
                <p class="text-sm text-gray-500">Are you sure you want to deactivate your account? All of your data will be permanently removed. This action cannot be undone.</p>
              </div>
            </div>
          </div>
        </div>
        <div class="bg-gray-50 px-4 py-3 sm:flex sm:flex-row-reverse sm:px-6">
          <button type="button" class="inline-flex w-full justify-center rounded-md bg-red-600 px-3 py-2 text-sm font-semibold text-white shadow-sm hover:bg-red-500 sm:ml-3 sm:w-auto">Deactivate</button>
          <button type="button" class="mt-3 inline-flex w-full justify-center rounded-md bg-white px-3 py-2 text-sm font-semibold text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 hover:bg-gray-50 sm:mt-0 sm:w-auto">Cancel</button>
        </div>
      </div>
    </div>
  </div>
</div>

                <div className="mx-auto max-w-8xl px-4 py-6 sm:px-6 lg:px-8 bg-slate-50">
                    <div className="grid grid-cols-1 md:grid-cols-1 gap-4 h-full">
                        <div className="bg-inherit p-4 h-full">
                            <div className='font-mono font-semibold'>
                                <h1 className="text-center text-3xl font-semibold font-mono">Manage Students</h1>
                                <div className="w-full p-4 mx-auto">
                                    <div className="relative overflow-x-auto">
                                        <div className="flex flex-wrap justify-between">
                                            <p></p>
                                            <a href="/admin/add-student">
                                                <Button type='button' text='Add' />
                                            </a>
                                        </div>

                                        <table className="w-full text-sm text-left rtl:text-right text-gray-500 dark:text-gray-400">
                                            <thead className="text-xs text-gray-700 uppercase bg-gray-50 dark:bg-gray-700 dark:text-gray-400">
                                                <tr>
                                                    <th scope="col" className="px-6 py-3">
                                                        Student ID
                                                    </th>
                                                    <th scope="col" className="px-6 py-3">
                                                        Student Image
                                                    </th>
                                                    <th scope="col" className="px-6 py-3">
                                                        Firstname
                                                    </th>
                                                    <th scope="col" className="px-6 py-3">
                                                        Lastname
                                                    </th>
                                                    <th scope="col" className="px-6 py-3">
                                                        Username
                                                    </th>
                                                    <th scope="col" className="px-6 py-3">
                                                        Role
                                                    </th>
                                                    <th scope="col" className="px-6 py-3 text-center">
                                                        Action
                                                    </th>
                                                </tr>
                                            </thead>
                                            <tbody>
                                                {users.map(student => (
                                                    <tr key={student._id}  className="bg-white border-b dark:bg-gray-800 dark:border-gray-700">
                                                        <th scope="row" className="px-6 py-4 font-medium text-gray-900 whitespace-nowrap dark:text-white">
                                                            {student.idno}
                                                        </th>
                                                        <td className="px-6 py-4">
                                                            {student.image}
                                                        </td>
                                                        <td className="px-6 py-4">
                                                            {student.firstname}
                                                        </td>
                                                        <td className="px-6 py-4">
                                                            {student.lastname}
                                                        </td>
                                                        <td className="px-6 py-4">
                                                            {student.username}
                                                        </td>
                                                        <td className="px-6 py-4">
                                                            {student.role}
                                                        </td>
                                                        <td className="px-6 py-4 flex flex-wrap justify-center">
                                                            <Link to={`/admin/edit-student/${student._id}`}>
                                                                <Button type='button' text='Edit' />
                                                            </Link>
                                                            <Button type='button' text='Delete' />
                                                        </td>
                                                    </tr>
                                                ))}
                                            </tbody>
                                        </table>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </main>
        </div>
    )
}
