import React from 'react'
import { useForm } from "react-hook-form";
const { register, handleSubmit, watch, formState: { errors } } = useForm();
const onSubmit = data => console.log(data);

console.log(watch("example")); 

export default function LoginPage() {
  return (
    <div>
      <main>
        <div className="mx-auto max-w-8xl px-4 py-6 sm:px-6 lg:px-8 bg-slate-50">
            <div className="grid grid-cols-1 md:grid-cols-1 gap-4">
                <div className="bg-inherit p-4">
                  <div className='text-center font-mono font-semibold'>
                    <h1 className="text-3xl font-semibold font-mono">Login Page</h1>
                    <div className="mt-5">
                      <form onSubmit={handleSubmit(onSubmit)}>
                        {/* register your input into the hook by invoking the "register" function */}
                        <input defaultValue="test" {...register("example")} />
                        
                        {/* include validation with required or other standard HTML validation rules */}
                        <input {...register("exampleRequired", { required: true })} />
                        {/* errors will return when field validation fails  */}
                        {errors.exampleRequired && <span>This field is required</span>}
                        
                        <input type="submit" />
                      </form>
                    </div>
                  </div>
                </div>
            </div>
        </div>
      </main>
    </div>
  )
}
