import React from 'react'
import { useForm } from "react-hook-form";
import InputField from '../../components/Form/InputField';
import Button from '../../components/Form/Button';
import Label from '../../components/Form/Label';
import { useCreateUser } from '../../hooks/useUsers';

export default function Add() {
    const { 
        // watch -> for realtime onchanges
        register, handleSubmit, formState: { errors } 
    } = useForm({
        defaultValues:{
            idno: '',
            firstname: '',
            lastname: '',
            image: null,
            username: '',
            password: '',
            role: ''
        }
    });

    const { result, loading, error, createNewUser } = useCreateUser();

    const onSubmit = async (data) => {
        await createNewUser(data);
    
        if (result?.status === 'success') {
          alert(result.message);
        } else if (error) {
          alert(`Error: ${error}`);
        }
    
        console.log(data);
    };


    if (loading) {
        return <div>Loading....</div>
    }

    if (error) {
        return <div>Error: {error}</div>
    }
    // const firstname = watch('firstname');
    // const lastname = watch('lastname');
    // console.log('fname: ', firstname, 'lastname: ',lastname)

  return (
    <div>
        <main>
            <div className="mx-auto max-w-8xl px-4 py-6 sm:px-6 lg:px-8 bg-slate-50">
                <div className="grid grid-cols-1 md:grid-cols-1 gap-4 h-full">
                    <div className="bg-inherit p-4 h-full">
                        <div className='font-mono font-semibold'>
                            <div className="w-1/2 p-4 mx-auto">
                            <div className="flex justify-between">
                                <h1 className="text-center text-3xl font-semibold font-mono">Add Students</h1>
                                <a href="/admin/"> <Button type='button' text='Back' /> </a>
                            </div>
                            <div id="alert-border-1" class="flex items-center p-4 mb-4 text-blue-800 border-t-4 border-blue-300 bg-blue-50 dark:text-blue-400 dark:bg-gray-800 dark:border-blue-800" role="alert">
    <svg class="flex-shrink-0 w-4 h-4" aria-hidden="true" xmlns="http://www.w3.org/2000/svg" fill="currentColor" viewBox="0 0 20 20">
      <path d="M10 .5a9.5 9.5 0 1 0 9.5 9.5A9.51 9.51 0 0 0 10 .5ZM9.5 4a1.5 1.5 0 1 1 0 3 1.5 1.5 0 0 1 0-3ZM12 15H8a1 1 0 0 1 0-2h1v-3H8a1 1 0 0 1 0-2h2a1 1 0 0 1 1 1v4h1a1 1 0 0 1 0 2Z"/>
    </svg>
    <div class="ms-3 text-sm font-medium">
      A simple info alert with an <a href="#" class="font-semibold underline hover:no-underline">example link</a>. Give it a click if you like.
    </div>
