import { fetchWithAuth } from "./authServices";

const API_URL = process.env.REACT_APP_API_URL;
const token = localStorage.getItem('accessToken');

// console.log('token: ',token)

// 
export const getUsers = async () => {
  try {
    const result = await fetchWithAuth(`${API_URL}/api/users/`, {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      },
    });

    return result.data;
  } catch (error) {
    console.error('Error fetching users:', error.message);
    throw new Error('Failed to fetch user profile');
  }
};

export const getUser = async (id) => {
    try {
      const result = await fetchWithAuth(`${API_URL}/api/users/${id}`, {
          method: 'GET',
          headers: {
              'Content-Type': 'application/json',
          },
      });

      if (!result || !result.data) {
        throw new Error('User data is not available');
      }

      return result.data;
    } catch (error) {
        console.error('Error fetching users:', error);
        throw new Error('Failed to fetch user profile');    
    }
}

export const createUser = async (data) => {
    try {
      const result = await fetchWithAuth(`${API_URL}/api/users/create`, {
        method: 'POST',
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(data)
      });
  
      if (!result || !result.message || !result.status) {
        throw new Error('Invalid response from server');
      }

      return {
        message: result.message,
        status: result.status
      };
      
    } catch (error) {
      console.error('Error creating user:', error);
      throw new Error ('Failed to create new user');
    }
}  

export const editUser = async (id, data) => {
  try {
    const res = await fetch(`${API_URL}/api/users/update/${id}`, {
      method: 'PUT',
      headers: {
        "Content-Type": "application/json",
        "Authorization": `Bearer ${token}`
      },
      body: JSON.stringify(data)
    })

    if (!res.ok) {
      throw new Error('Failed to update user');
    }
    const result = await res.json()
    return {
      status: result.status,
      message: result.message
    }
  } catch (error) {
    console.error('Error updating user: ', error)
    throw error;
  }
}

export const deleteUser = async (id) => {
  try {
    const res = await fetch(`${API_URL}/api/users/archive`, {
      method: 'DELETE',
      headers: {
        "Content-Type": "application/json",
        "Authorization": `Bearer ${token}`
      },
      body: JSON.stringify({ id })
    })

    if (!res.ok) {
      throw new Error('Failed to update user');
    }

    const result = await res.json()
    return{ 
      status: result.status,
      message: result.message
    }
  } catch (error) {
    console.error('Error deleting user: ',error)
    throw error;
  }
}
