import React from 'react'
import { useForm } from "react-hook-form";

export default function LoginPage() {
  const { 
    register, handleSubmit, watch, formState: { errors } 
  } = useForm({
    defaultValues:{
      username: '',
      password: ''
    }
  });
  
  const username = watch("username");
  const password = watch('password');

  return (
    <div>
      <main>
        <div className="mx-auto max-w-8xl px-4 py-6 sm:px-6 lg:px-8 bg-slate-50">
            <div className="grid grid-cols-1 md:grid-cols-1 gap-4">
                <div className="bg-inherit p-4">
                  <div className='font-mono font-semibold'>
                    <h1 className="text-center text-3xl font-semibold font-mono">Login Page</h1>
                    <div className="w-1/2 mt-5 flex justify-center items-center">
                        <form onSubmit={handleSubmit((data) => {
                          console.log(data)
                        })}>
                            <input {...register("username", { required: "Usernames is required" })} placeholder='Username' />
                              <p>{errors.username?.message}</p>
                            <input {...register("password", { required: "Password is required" })} placeholder='Password' />
                              <p>{errors.password?.message}</p>
                            <input type="submit" />
                        </form>
                    </div>
                  </div>
                </div>
            </div>
        </div>
      </main>
    </div>
  )
}
