import { useState, useEffect } from 'react';
import { createUser, getUser, getUsers } from '../services/adminService';

export const useUsers = () => {
  const [users, setUsers] = useState([]);
  const [loading, setloading] = useState(true);
  const [error, seterror] = useState(null);

  useEffect(() => {
    let isMounted = true;

    const fetchUsers = async () => {
      try {
        const data = await getUsers();
        if (isMounted) {
          setUsers(data)
          setloading(false)
        }
      } catch (error) {
        if (isMounted) {
          seterror(error.message)
          setloading(false)
        }
      }
    }
    
    fetchUsers();

    return () => {
      isMounted = false;
    }
  }, []);
  return { users, loading, error }; 
}

export const useUser = (id) => {
  const [user, setuser] = useState([]);
  const [loading, setloading] = useState(true);
  const [error, seterror] = useState(null);

  useEffect(() => {
    let isMounted = true;

    const fetchUserinfo = async () => {
      try {
        const data = await getUser(id);
        if (isMounted) {
          setuser(data)
          setloading(false)
        }
      } catch (error) {
        if (isMounted) {
          seterror(error.message)
          setloading(false)
        }
      }
    }
    
    fetchUserinfo();

    return () => {
      isMounted = false;
    }
  }, [id]);
  return { user, loading, error }; 
}

export const useCreateUser = () => {
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const createNewUser = async (data) => {
    setLoading(true);
    try {
      const response = await createUser(data);
      setResult(response);
      // setError(null)
    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
      // setResult(null);
    }
  };

  return { result, loading, error, createNewUser};
};

export const userUpdateuser = async () => {
  const [result, setresult] = useState(null);
  const [loading, setloading] = useState(false);
  const [error, seterror] = useState(null);

  const updateOldUser = async (data) => {
    setloading(true);
    try {
      const updateUser = await userUpdateuser(data);
      setresult()
    } catch (error) {
      seterror(error)
    } finally{
      setloading(false)
    }
  }
  
}
