import React from 'react'
import { useForm } from "react-hook-form";
import InputField from '../../components/Form/InputField';
import Button from '../../components/Form/Button';
import Label from '../../components/Form/Label';

export default function RegisterPage() {
    const { 
        register, handleSubmit, watch, formState: { errors } 
        } = useForm({
        defaultValues:{
            firstname: '',
            lastname: ''
        }
        });
        
        const onSubmit = data => console.log(data);
        // const username = watch('username');
        // const password = watch('password');

  return (
    <div>
        <main>
            <div className="mx-auto max-w-8xl px-4 py-6 sm:px-6 lg:px-8 bg-slate-50 h-[80vh]">
                <div className="grid grid-cols-1 md:grid-cols-1 gap-4 h-full">
                    <div className="bg-inherit p-4 h-full">
                        <div className='font-mono font-semibold'>
                            <h1 className="text-center text-3xl font-semibold font-mono">Register as Student</h1>
                            <div class="w-1/2 p-4 mx-auto">
                                <form onSubmit={handleSubmit}>
                                    <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 lg:grid-cols-2 gap-4">
                                        <div className="p-4">
                                            <Label htmlFor='firstname' text='Firstname' />
                                            <InputField
                                            type="text"
                                            placeholder="Firstname"
                                            name="firstname"
                                            register={register}
                                            validation={{ required: 'Firstname is required' }}
                                            />
                                            <p className="text-red-500">{errors.firstname?.message}</p>
                                        </div>
                                        <div className="p-4">
                                            <Label htmlFor='lastname' text='Lastname' />
                                            <InputField
                                            type="text"
                                            placeholder="Lastname"
                                            name="lastname"
                                            register={register}
                                            validation={{ required: 'Firstname is required' }}
                                            />
                                            <p className="text-red-500">{errors.firstname?.message}</p>
                                        </div>
                                    </div>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </main>
    </div>
  )
}
