import { useState } from 'react';
import { loginUser, logoutUser } from '../services/authServices';

export const useLoginuser = () => {
    const [result, setResult] = useState(null);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);

    const signInUser = async (data) => {
      setLoading(true);
      try {
        const response = await loginUser(data);
        setResult(response);
        // console.log('response: ',response);
        // localStorage.setItem('accessToken', response.accesToken);

      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };
  
    return { result, loading, error, signInUser };
};

export const userLogoutuser = () => {
  const [message, setMessage] = useState(null);
  const [status, setStatus] = useState(null);
  const [logoutError, setLogoutError] = useState(null);

  const userLogout = async () => {
    try {
      await logoutUser();
      setMessage('Logout successful')
      setLogoutError(null)
      setStatus('success')
      localStorage.removeItem('accessToken')
      
    } catch (error) {
      setLogoutError(error)
      setStatus('failed')
    }
  };
  return { message, status, logoutError, userLogout }
}
