const API_URL = process.env.REACT_APP_API_URL;
const token = localStorage.getItem('accessToken');

console.log('token: ',token)

export const loginUser = async (data) => {
  try {
    const res = await fetch(`${API_URL}/api/auth/login`, {
        method: 'POST',
        headers: {
            "Content-Type": "application/json",
        },
        credentials: 'include',
        body: JSON.stringify(data)    
    })
    
    if (!res.ok) {
        throw new Error('Failed to fetch user profile');
    }

    const result = await res.json()
    return { 
        message: result.message,
        status: result.status,
        accesToken: result.accessToken,
        userRole: result.userRole
    }
} catch (error) {
    console.log('Error signing in: ', error);
    throw error;
  }
}

export const refreshAccessToken = async () => {
  try {
    const refreshToken = document.cookie.split('; ').find(row => row.startsWith('refreshToken='));
    console.log('Refresh Token:', refreshToken ? refreshToken.split('=')[1] : 'Not available');

    
    const res = await fetch(`${API_URL}/api/refresh`, {
      method: 'GET',
      credentials: 'include', 
    });

    if (!res.ok) throw new Error('Failed to refresh token');

    const { accessToken } = await res.json();
    localStorage.setItem('accessToken', accessToken); 
    return accessToken;
  } catch (error) {
    console.error('Error refreshing token:', error);
    throw error;
  }
};

export const fetchWithAuth = async (url, options = {}) => {
  try {
    let accessToken = localStorage.getItem('accessToken');

    if (!accessToken) throw new Error('No access token found');

    options.headers = {
      ...options.headers,
      'Authorization': `Bearer ${accessToken}`,
    };

    let res = await fetch(url, options);

    if (res.status === 401) {
      await refreshAccessToken(); 
      accessToken = localStorage.getItem('accessToken'); 

      options.headers['Authorization'] = `Bearer ${accessToken}`;
      res = await fetch(url, options);
    }

    if (!res.ok) {
      const errorMessage = await res.text(); 
      throw new Error(`Request failed with status ${res.status}: ${errorMessage}`);
    }

    return await res.json();
  } catch (error) {
    console.error('Error fetching with auth:', error);
    throw error;
  }
};

export const logoutUser = async () => {
  try {
    if (!token) throw new Error('Logout failed')

    const res = await fetch(`${API_URL}/api/logout`, {
        method: 'GET',
        headers: {
            "Content-Type": "application/json",
            "Authorization": `Bearer ${token}`
        }
    });
    if (!res.ok) throw new Error('Logout failed');

    await res.json()
    return {
        status: 'success',
        message: 'Logout successfully'
    }
  } catch (error) {
    console.log('Error signing out: ', error)
    throw error;
  }
}
